function formatDate(date){
    return date.toLocaleDateString();
}

function Avatar(props) {
    const [count, setCount] = useState(0);
    const [desc, setDesc]  = useState("이미지 클릭 카운트");
    const [contents, setContents] = useState([{ text: "아무문장1" }]);
    
    useEffect(() => {
        console.log("componentDidMount와 유사함");
    }, []);
    useEffect(() => {
        console.log("count가 변경됨");
    }, [count]);
    useEffect(() => {
        console.log("desc가 변경됨");
    }, [desc]);

    const { user } = props;
    return <>
        <img className="Avatar"
            src={user.avatarUrl}
            alt={user.name + count}
            onClick={() => {
                setCount(count + 1);
            }}
        />
        <button onClick={() => {
            setDesc(Math.random().toString(36).substring(7))
        }}>DESC 변경</button>
        <p>( {desc + ": " + count} )</p>
        <p>{contents.text}</p>
    </>
  }

function UserInfo(props) {
    return (
        <div className="UserInfo">
            <Avatar user={props.user} />
            <div className="UserInfo-name">
                {props.user.name}
            </div>
        </div>
    );
}

function Comment(props) {
  return (
      <div className="Comment">
          <UserInfo user={props.author} />
          <div className="Comment-text">
              {props.text}
          </div>
          <div className="Comment-date">
              {formatDate(props.date)}
          </div>
      </div>
  );
}

